package com.byon.gorilla.algorithms;

import org.junit.Test;

import static org.junit.jupiter.api.Assertions.*;

public class CountSubstringsTest {

    @Test
    public void happy_path(){
        String input = "";
        int result = CountSubstrings.countSubstrings(input);
        assertEquals(result, 1664);
    }

    @Test
    public void rival_happy_path(){
        String input = "";
        int result = CountSubstrings.rivalCountSubstrings(input);
        assertEquals(result, 1664);
    }

}